DROP FUNCTION IF EXISTS `avg_amount_transactions`;
delimiter ;;
CREATE FUNCTION `avg_amount_transactions`(amount double)
 RETURNS double
BEGIN
	DECLARE average DOUBLE DEFAULT 0;
	DECLARE CONTINUE HANDLER FOR NOT FOUND RETURN average;
	LOOP
		FETCH GROUP NEXT ROW;
		SET average = average+amount;
	END LOOP;
END
;;
delimiter ;

