-- Recursive CTE

CREATE OR REPLACE VIEW v_recursive_cte AS
WITH RECURSIVE t_transfer AS(SELECT * FROM transfer ORDER BY transfer_date)

SELECT account.name FROM account INNER JOIN t_transfer ON t_transfer.recipient_account_id=account.account_id 
UNION 
SELECT account.name FROM account INNER JOIN t_transfer ON t_transfer.owner_account_id=account.account_id;
